import React, { useState } from "react";

function SignUp() {
    const [formData, setFormData] = useState({ name: "", email: "", password: "" });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!formData.email || !formData.password) {
            alert("Please fill in email and password!");
            return;
        }
        // Simple alert for demonstration
        alert(`Account created for ${formData.email}!`);
        setFormData({ name: "", email: "", password: "" });
    };

    return (
        <div className="row justify-content-center py-5">
            <div className="col-md-6">
                <h2 className="text-center mb-4">Join Iris & Lo</h2>
                {/* The p-4 shadow-lg bg-light rounded classes provide the theme */}
                <form onSubmit={handleSubmit} className="p-4 shadow-lg bg-light rounded">
                    <div className="mb-3">
                        <label className="form-label">Full Name</label>
                        <input type="text" name="name" className="form-control"
                            value={formData.name} onChange={handleChange} />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Email Address</label>
                        <input type="email" name="email" className="form-control"
                            value={formData.email} onChange={handleChange} required />
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Password</label>
                        <input type="password" name="password" className="form-control"
                            value={formData.password} onChange={handleChange} required />
                    </div>
                    {/* btn-dark aligns with a simple, contrasting theme */}
                    <button type="submit" className="btn btn-dark w-100">Sign Up</button>
                </form>
            </div>
        </div>
    );
}

export default SignUp;
