import React, { useEffect, useState } from "react";

function Cart() {
  const [cart, setCart] = useState([]);

  // ‚úÖ Load cart when the component mounts
  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  // ‚úÖ Fix: use the `cart` from state (not undefined)
  const handleRemove = (id) => {
    const updatedCart = cart.filter((item) => item._id !== id);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const handleCheckout = async () => {
    const order = {
      customerName: "Demo User",
      products: cart.map((item) => ({
        productId: item._id,
        quantity: item.quantity,
      })),
      totalAmount: cart.reduce(
        (sum, item) => sum + item.price * item.quantity,
        0
      ),
      status: "Received",
    };

    try {
      const res = await fetch("http://localhost:5000/api/orders", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(order),
      });

      if (res.ok) {
        alert("‚úÖ Order placed successfully!");
        localStorage.removeItem("cart");
        setCart([]);
      } else {
        alert("‚ùå Failed to place order. Try again.");
      }
    } catch (err) {
      console.error(err);
      alert("‚ö†Ô∏è Server error while placing order.");
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4 text-center">üõí Your Shopping Cart</h2>
      {cart.length === 0 ? (
        <p className="text-center">Your cart is empty.</p>
      ) : (
        <>
          {cart.map((item) => (
            <div
              key={item._id}
              className="d-flex justify-content-between align-items-center mb-3 border-bottom pb-2"
            >
              <div>
                <img
                  src={item.image}
                  alt={item.title}
                  width="50"
                  height="50"
                  style={{ objectFit: "contain" }}
                />
                <strong className="ms-2">{item.title}</strong> (x{item.quantity})
              </div>
              <div>
                ‚Çπ{item.price * item.quantity}
                <button
                  className="btn btn-sm btn-danger ms-3"
                  onClick={() => handleRemove(item._id)}
                >
                  Remove
                </button>
              </div>
            </div>
          ))}

          <div className="text-center mt-4">
            <h4>
              Total: ‚Çπ
              {cart.reduce(
                (sum, item) => sum + item.price * item.quantity,
                0
              )}
            </h4>
            <button className="btn btn-success mt-3" onClick={handleCheckout}>
              Checkout üõçÔ∏è
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default Cart;
