import React from "react";
import ProductCard from "../components/ProductCard";

function Home() {
  return (
    <div>
      {/* Iris & Lo Banner */}
      <div
        className="text-center my-5"
        style={{
          backgroundColor: '#FADADD',
          padding: '60px 20px',
          borderRadius: '15px'
        }}
      >
        <h1 style={{ fontFamily: "'Dancing Script', cursive", fontSize: '3rem' }}>
          Iris & Lo
        </h1>
        <p style={{ fontSize: '1.2rem' }}>
          Elegant Jewellery for Every Occasion
        </p>
      </div>

      {/* Featured Products Section */}
      <h3 className="mb-3">Featured Products</h3>
      <div className="row g-4">
        <div className="col-md-4">
          <ProductCard image="https://via.placeholder.com/200" title="Diamond Ring" price="50000" />
        </div>
        <div className="col-md-4">
          <ProductCard image="https://via.placeholder.com/200" title="Gold Necklace" price="75000" />
        </div>
        <div className="col-md-4">
          <ProductCard image="https://via.placeholder.com/200" title="Silver Bracelet" price="10000" />
        </div>
      </div>
    </div>
  );
}

export default Home;
