import React, { useEffect, useState } from "react";

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    price: "",
    image: "",
    status: "In Stock",
  });

  const API_URL = "http://localhost:5000/api/products";

  // ðŸŸ¡ Fetch all products
  const fetchProducts = async () => {
    try {
      const res = await fetch(API_URL);
      const data = await res.json();
      setProducts(data);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  // ðŸŸ¢ Add new product
  const addProduct = async (e) => {
    e.preventDefault();
    try {
      await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newProduct),
      });
      setNewProduct({ name: "", description: "", price: "", image: "", status: "In Stock" });
      fetchProducts();
    } catch (error) {
      console.error("Error adding product:", error);
    }
  };

  // ðŸ”µ Update product status
  const toggleStatus = async (id, currentStatus) => {
    try {
      await fetch(`${API_URL}/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: currentStatus === "In Stock" ? "Sold Out" : "In Stock" }),
      });
      fetchProducts();
    } catch (error) {
      console.error("Error updating product:", error);
    }
  };

  // ðŸ”´ Delete product
  const deleteProduct = async (id) => {
    try {
      await fetch(`${API_URL}/${id}`, { method: "DELETE" });
      fetchProducts();
    } catch (error) {
      console.error("Error deleting product:", error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div className="admin-dashboard" style={{ padding: "2rem" }}>
      <h1>ðŸ›  Admin Product Management</h1>

      {/* Add Product Form */}
      <form onSubmit={addProduct} style={{ marginBottom: "2rem" }}>
        <input
          type="text"
          placeholder="Name"
          value={newProduct.name}
          onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Description"
          value={newProduct.description}
          onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
          required
        />
        <input
          type="number"
          placeholder="Price"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Image URL"
          value={newProduct.image}
          onChange={(e) => setNewProduct({ ...newProduct, image: e.target.value })}
        />
        <button type="submit">Add Product</button>
      </form>

      {/* Display Products */}
      <div>
        {products.map((product) => (
          <div
            key={product._id}
            style={{
              border: "1px solid #ccc",
              padding: "1rem",
              marginBottom: "1rem",
              borderRadius: "10px",
            }}
          >
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>ðŸ’° â‚¹{product.price}</p>
            <p>Status: {product.status}</p>
            <button onClick={() => toggleStatus(product._id, product.status)}>
              Toggle Status
            </button>
            <button onClick={() => deleteProduct(product._id)} style={{ marginLeft: "1rem" }}>
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
