import React, { useState } from "react";
import { Link } from "react-router-dom"; // Import Link to enable navigation

function ProductCard({ image, title, price }) {
  // CONCEPTUAL STATE: In a full application, replace this with a prop or global state check
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // NOTE: For demonstration purposes, you can manually change 'isLoggedIn' 
  // to 'true' to see the "Add to Cart" button, or keep it 'false' to see 
  // the "Sign Up to Buy" button.

  const handleAddToCart = () => {
    if (isLoggedIn) {
      alert(`${title} added to cart!`);
      // Real implementation would update global cart state
    } else {
      // If not logged in, prompt the user.
      alert("Please sign up or log in to add items to the cart.");
      // In a real app, you would use navigation logic here:
      // navigate('/signup'); 
    }
  };

  return (
    <div className="card shadow-sm product-card">
      <img src={image} className="card-img-top" alt={title} />
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text">â‚¹{price}</p>
        <div className="product-card-button">

          {isLoggedIn ? (
            // User IS logged in: show the Add to Cart button
            <button
              className="btn btn-pink w-100"
              onClick={handleAddToCart}
            >
              Add to Cart
            </button>
          ) : (
            // User IS NOT logged in: show the Sign Up prompt/link
            <Link
              to="/signup" // Links to the new Sign Up page
              className="btn btn-pink w-100"
              onClick={() => handleAddToCart()} // Triggers the alert first
            >
              Sign Up to Buy
            </Link>
          )}

        </div>
      </div>
    </div>
  );
}

export default ProductCard;
