// src/components/ProductCard.js

import React from "react";
import { Link } from "react-router-dom";
// ðŸ›‘ UPDATED PATH: Import hook from the src directory
import { useAuth } from "../AuthContext";

function ProductCard({ image, title, price }) {
  // ðŸ›‘ KEY CHANGE: Get isLoggedIn from the global context
  const { isLoggedIn } = useAuth();

  const handleAddToCart = () => {
    if (isLoggedIn) {
      alert(`${title} added to cart!`);
    } else {
      alert("Please sign up or log in to add items to the cart.");
    }
  };

  return (
    <div className="card shadow-sm product-card">
      <img src={image} className="card-img-top" alt={title} />
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text">â‚¹{price}</p>
        <div className="product-card-button">

          {isLoggedIn ? (
            // User IS logged in: show the Add to Cart button
            <button
              className="btn btn-pink w-100"
              onClick={handleAddToCart}
            >
              Add to Cart
            </button>
          ) : (
            // User IS NOT logged in: show the Sign Up prompt/link
            <Link
              to="/signup" // Links to the Sign Up page
              className="btn btn-pink w-100"
              onClick={handleAddToCart} // Triggers the alert before navigating
            >
              Sign Up to Buy
            </Link>
          )}

        </div>
      </div>
    </div>
  );
}

export default ProductCard;
